import React from 'react';
import { Row, Container, Col } from 'react-bootstrap';

import '../App.css';

const CharacterStyles = {
    // color: 'white',
    // borderColor: characterBorderColor,
    borderWidth: '1px',
    borderStyle: 'dashed',
    characterImage: {
        width: '200px',
        height: 'auto',
        borderStyle: 'dashed',
        borderColor: 'black',
        borderWidth: '2px'
    }
}

const Characters = props => {
    let image_holder1 = [];
    let image_holder2 = [];
    let image_holder3 = [];
    let characterBorderColor = {borderColor: props.characterBorderColor}
    console.log(characterBorderColor.borderColor)

    props.characters.forEach((character, idx) => {
        if(idx <= 2){
            image_holder1.push(
                <Col lg={3} key={idx}>
                    <img 
                        src={character.src} 
                        alt={character.alt} 
                        className={character.hover ? 'character_image my-2 animated pulse': 'character_image my-2'}
                        style={CharacterStyles.characterImage}
                        onClick={() => props.handleImageClick(character.alt)}
                        onMouseEnter={() => props.toggleHover(character.alt)}
                    />
                </Col>
            )
        }else if(idx > 2 && idx <= 5){
            image_holder2.push(
                <Col lg={3} key={idx}>
                    <img 
                        src={character.src} 
                        alt={character.alt} 
                        className={character.hover ? 'character_image my-2 animated pulse': 'character_image my-2'}
                        style={CharacterStyles.characterImage}
                        onClick={() => props.handleImageClick(character.alt)}
                        onMouseEnter={() => props.toggleHover(character.alt)}
                    />
                </Col>
            )
        }else if(idx > 5){
            image_holder3.push(
                <Col lg={3} key={idx}>
                    <img 
                        src={character.src} 
                        alt={character.alt} 
                        className={character.hover ? 'character_image my-2 animated pulse': 'character_image my-2'}
                        style={CharacterStyles.characterImage}
                        onClick={() => props.handleImageClick(character.alt)}
                        onMouseEnter={() => props.toggleHover(character.alt)}
                    />
                </Col>
            )
        }
    })

    return (
        <div style={[CharacterStyles, ]} className='mt-2 mx-auto text-center'>
            <Container>
                {image_holder1.length === 3 && image_holder2.length === 3 && image_holder1.length === 3 &&
                    <div>
                        <Row className='justify-content-center'>
                            {image_holder1}
                        </Row>
                        <Row className='justify-content-center'>
                            {image_holder2}
                        </Row>
                        <Row className='justify-content-center'>
                            {image_holder3}
                        </Row>
                    </div>
                }
            </Container>
        </div>
    )
}

export default Characters;
